    if (!product){
        return <div> Product Not Found</div>
    }
    return (
        <div className="row">
          <div className="col-2"> 
           <img className="large" src={Product.image} alt={Product.name}>
               </img>
               </div>
          <div className="col-1">

          </div>
          <div className="col-1"> 
          </div>

        </div>
    )
}
